#include <iostream>#include <ctime>using namespace std;int main(){	int heure, min, sec, jour, mois, annee;	bool heureEte;	// OBTENTION DE LA DATE ACTUELLE	time_t dateActuelle = time(NULL);	cout << "dateActuelle : " << dateActuelle << endl;		// construction de la structure :	struct tm *maintenant = localtime(&dateActuelle);	// mise à jour des variables :	if (maintenant->tm_isdst > 0)		heureEte = true;	else // maintenant->tm_isdst < 0		heureEte = false;	heure = maintenant->tm_hour;	min = maintenant->tm_min;	sec = maintenant->tm_sec;	jour = maintenant->tm_mday;	mois = maintenant->tm_mon + 1;		// les mois sont donnée de 0 à 11	annee = 1900 + maintenant->tm_year;	// les années sont données en comptant à partir de 1900 = 0		cout << " heure = " << heure << endl;	cout << " minutes = " << min << endl;	cout << " seconde = " << sec << endl;	cout << " jour = " << jour << endl;	cout << " mois = " << mois << endl;	cout << " annee = " << annee << endl;	if (heureEte)		cout << "c'est l'heure d'ete" << endl;	else		cout << "c'est l'heure d'hiver" << endl;		cout << "c'est le " << maintenant->tm_wday << " jour de la semaine" << endl;	// jour de la semaine : dimanche = 0	cout << "c'est le " << maintenant->tm_yday + 1 << " jour de l'annee" << endl;	// jour de l'année : 1er janvier = 0		return 0;}